mapper.select:
SELECT T1.idx ,DATE_FORMAT(T1.workDateTime,'%Y-%m-%d %T') as workDateTime ,T1.workIp ,Case When T1.workUserId ='' Then 'system' Else T1.workUserId End workUserId ,Case When T1.workUserId ='' Then T1.workIp Else T4.userName End workUserName ,T4.groupId ,T5.groupName as workGroupName ,T1.workCategory ,T2.codeName as workCategoryName ,T1.workCode ,T3.codeName as workCodeName ,T3.description as description ,ifnull(T1.workKey1,'N') as workKey1 ,ifnull(T1.workKey2,'N') as workKey2 ,ifnull(T1.workKey3,'N') as workKey3 ,T1.searchKey1 ,T6.companyName FROM adWorkHistory T1 Left Join ofCode T2 On T1.workCategory=T2.codeId And T2.codeGroupId = 'SE01' Left Join ofCode T3 On T1.workCode=T3.codeId And T3.codeGroupId = 'SE02' Left Join ofUser T4 On T1.workUserId=T4.userId Left Join ofGroup T5 On T4.groupId=T5.groupId Left Join adCompany T6 On T1.searchKey1=T6.companyCode WHERE T1.workDateTime between concat(#{start_workDate},' 00:00:00') and concat(#{end_workDate},' 23:59:59')
AND T4.groupId = #{con_groupId}
AND T1.workCategory = #{con_workCategory}
AND T1.workCode = #{con_workCode}
AND T4.userName LIKE CONCAT('%', #{searchValue}, '%')
AND T1.workUserId LIKE CONCAT('%', #{searchValue}, '%')
AND T1.workKey1 LIKE CONCAT('%', #{searchValue}, '%')
AND T6.companyName LIKE CONCAT('%', #{searchKey1Value}, '%')
AND T1.searchKey1 LIKE CONCAT('%', #{searchKey1Value}, '%')
ORDER BY T1.workDateTime desc LIMIT ${page_limit_val1} , ${page_limit_val2}

SELECT count(*) AS TOT_COUNT FROM adWorkHistory T1 Left Join ofCode T2 On T1.workCategory=T2.codeId And T2.codeGroupId = 'SE01' Left Join ofCode T3 On T1.workCode=T3.codeId And T3.codeGroupId = 'SE02' Left Join ofUser T4 On T1.workUserId=T4.userId Left Join ofGroup T5 On T4.groupId=T5.groupId Left Join adCompany T6 On T1.searchKey1=T6.companyCode WHERE T1.workDateTime between concat(#{start_workDate},' 00:00:00') and concat(#{end_workDate},' 23:59:59')
AND T4.groupId = #{con_groupId}
AND T1.workCategory = #{con_workCategory}
AND T1.workCode = #{con_workCode}
AND T4.userName LIKE CONCAT('%', #{searchValue}, '%')
AND T1.workUserId LIKE CONCAT('%', #{searchValue}, '%')
AND T1.workKey1 LIKE CONCAT('%', #{searchValue}, '%')
AND T6.companyName LIKE CONCAT('%', #{searchKey1Value}, '%')
AND T1.searchKey1 LIKE CONCAT('%', #{searchKey1Value}, '%')
