mapper.insert:
		insert into adCompany (	
				companyCode
            	,companyName
            	,companyPhone 
            	,postCode
            	,address1
            	,faxNumber
           		,mobilePhone
            	,email
            	,email_cc
            	,chargeName
            	,createUserId
            	,createDateTime
            	,deletedYn
		)values(
				 #{companyCode}
	  			, #{companyName}
	  			, #{companyPhone}
	  			, #{postCode}
	  			, #{address1}
	  			, #{faxNumber}
	  			, #{mobilePhone}
	  		    , #{email}
	  		    , #{email_cc}
	  		    , #{chargeName}
	  			, #{createUserId}
	  			, now()
	  			, #{deletedYn}
		)
		ON DUPLICATE KEY UPDATE companyCode = #{companyCode},
		                        companyName = #{companyName},
		                        companyPhone = #{companyPhone},
		                        postCode = #{postCode},
		                        address1 = #{address1},
		                        faxNumber = #{faxNumber},
		                        mobilePhone = #{mobilePhone},
		                        email = #{email},
		                        email_cc = #{email_cc},
		                        chargeName = #{chargeName},
		                        updateUserId = #{updateUserId},
		                        updateDateTime =now(),
		                        deletedYn = #{deletedYn}
